nconf.env().file({ file: 'config.json' });

var connectionString = nconf.get("MONGOLAB_URI");

MongoClient.connect(connectionString, function (err, db) {
    if (err) throw err;

    if (!err) {
        console.log("Connected to 'pkgsdb' database");
        db.collection('pkgs', { strict: true }, function (err, collection) {
            if (err) {
                console.log("The 'pkgsdb' collection doesn't exist. Creating it with sample data...");
                populateDB(db);
            }
        });
    }

})


exports.findById = function (req, res) {
      var id = req.params.id;
      console.log('Retrieving pkg: ' + id);

      MongoClient.connect(connectionString, function (err, db) {
          if (err) throw err;

          if (!err) {

              db.collection('pkgs', function (err, collection) {
                  collection.findOne({ '_id': new BSON.ObjectID(id) }, function (err, item) {
                      res.send(item);
                  });
              });
          }
      })
  };
 
exports.findAll = function(req, res) {

    MongoClient.connect(connectionString, function(err, db) {
    if(err) throw err;

     if(!err) {
       db.collection('pkgs', function(err, collection) {
        collection.find().toArray(function(err, items) {
            res.send(items);
        });
    });
    }
   
  }) 
  

   
};

exports.addPkg = function (req, res) {
    var pkg = req.body;
    console.log('Adding pkgs: ' + JSON.stringify(pkg));

    MongoClient.connect(connectionString, function (err, db) {
        if (err) throw err;

        if (!err) {
            db.collection('pkgs', function (err, collection) {
                collection.insert(pkg, { safe: true }, function (err, result) {
                    if (err) {
                        res.send({ 'error': 'An error has occurred' });
                    } else {
                        console.log('Success: ' + JSON.stringify(result[0]));
                        res.send(result[0]);
                    }
                });
            });
        }
    })
};

exports.findRemote = function (req, res) {
    var body = "";
    var url = "http://storage.appsforazure.com/appsforazureobjectstest/servicepackages.json";
    http.get(url, function (res2) {

        res2.on('data', function (chunk) {
            body += chunk;

        });

        res2.on("end", function () {
            res.setHeader("Content-Type", "application/json");
            res.send(body);
        })

        res2.on("error", function (error) {
            res.send(error);
        })


    });
}

exports.updatePkg = function (req, res) {
    var id = req.params.id;
    var pkg = req.body;
    console.log('Updating pkgs: ' + id);
    console.log(JSON.stringify(pkg));

    MongoClient.connect(connectionString, function (err, db) {
        if (err) throw err;

        if (!err) {
            db.collection('pkgs', function (err, collection) {
                collection.update({ '_id': new BSON.ObjectID(id) }, pkg, { safe: true }, function (err, result) {
                    if (err) {
                        console.log('Error updating pkg: ' + err);
                        res.send({ 'error': 'An error has occurred' });
                    } else {
                        console.log('' + result + ' document(s) updated');
                        res.send(pkg);
                    }
                });
            });
        }

    })
};

exports.deletePkg = function (req, res) {
    var id = req.params.id;
    console.log('Deleting pkg: ' + id);

    MongoClient.connect(connectionString, function (err, db) {
        if (err) throw err;

        if (!err) {
            db.collection('pkgs', function (err, collection) {
                collection.remove({ '_id': new BSON.ObjectID(id) }, { safe: true }, function (err, result) {
                    if (err) {
                        res.send({ 'error': 'An error has occurred - ' + err });
                    } else {
                        console.log('' + result + ' document(s) deleted');
                        res.send(req.body);
                    }
                });
            });
        }
    })

}
 
